class Quickfix(Cell):

    def __init__(self):
        super(Quickfix, self).__init__()
        cells.emit({'type': 'quickfix_list_available', 'sender': self.id})

    def l_quickfix_list(self, event):
        self.reply_now(event, {'truncated': False, 'list': [{'bufnr': 1, 'text': 'error', 'col': 10, 'lnum': 5, 'type' : 'E'}]})


